mapper.select:
select * from t_sc_score where 1 = 1
and ${key} = #{item}
order by score

mapper.insert:

insert into t_sc_score (id, t_sc_student_id, t_sc_exam_id,
score,
rank_in_class, rank_in_grade
)
values (#{id,jdbcType=CHAR},
#{tScStudentId,jdbcType=CHAR},
#{tScExamId,jdbcType=CHAR},
#{score,jdbcType=REAL}, #{rank_in_class,jdbcType=INTEGER},
#{rank_in_grade,jdbcType=INTEGER}
)

mapper.select:
select * from t_sc_score
where
t_sc_exam_id = #{examId}
and
t_sc_student_id = #{studentId}

select sc.rank_in_class as ranking, sc.score as score, sc.id as id, e.name as examName, e.id as examId, cv.t_sc_curricula_id as curriculaId, cv.t_sc_teacher_id as teacherId, cv.t_sc_class_id as clazzId, s.name as studentName, s.IDCard as idCard from t_sc_score sc, t_sc_curricula_variable cv, t_sc_exam e, t_sc_student s where sc.t_sc_student_id = s.id and sc.t_sc_exam_id = e.id and e.t_sc_curricula_variable_id = cv.id and e.id = #{examId}
and sc.t_sc_student_id = #{studentId}
order by sc.score desc
